import React,{useEffect, useReducer} from 'react'
import './style.css'
   
const reducer=(state,action)=>{
    if(action.type==="INCREMENT")
    {
      state=state+1 
    }
    if(action.type==="DECR")
    {
      state=state-1 
    }
    return(state) ;
}
function Use() {
   {/* const [state, setstate] = useState(0) */ }
   const [state, dispatch] = useReducer(reducer, initialState)
    useEffect(()=>{
      document.title=`chat(${state})`
    })
    return (
        <>
             <div className="center_div">
        <p>{state}</p>
        <div class="button2" onClick={()=>dispatch({type:"INCREMENT"})}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          INCREMENT
        </div>
        <div class="button2"onClick={()=>dispatch({type:""})}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          DECR
        </div>
      </div>
        </>
    )
}

export default Use
